Ext.define('MyApp.controller.MyController', {
    extend: 'Ext.app.Controller',

    config: {
        refs: {
            detailPanel: 'mainview #detailPanel',
            primarychart: 'polar#primary-chart'
        },

        control: {
            "mainview #runButton": {
                tap: 'runAction'
            },
            "button#mybutton3": {
                tap: 'onButtonTap'
            },
            "button#mybutton4": {
                tap: 'onButtonTap1'
            }
        }
    },

    runAction: function(target) {
        this.loadPStore();

        // Create new model
        var model = Ext.create('MyApp.model.MyModel', {
            id: 123,
            text: 'Hello World'
        });

        // Bind model to view
        //this.getDetailPanel().setRecord(model);


    },

    onButtonTap: function(button, e, eOpts) {
        var me = this;
        websocket.send('1');
    },

    onButtonTap1: function(button, e, eOpts) {
        var me = this;
        websocket.send('0');
    },

    loadPStore: function() {

        pchart = this.getPrimarychart();
        var mstore = pchart.getStore();
        mstore.load();
    },

    init: function(application) {
        var me = this;
        websocket = Ext.create ('Ext.ux.WebSocket', {
            url: 'ws://captain-zero.tk:8080' ,
            listeners: {
                open: function (ws) {
                    console.log ('The websocket is ready to use');
                    ws.send ('This is a simple text');
                } ,
                close: function (ws) {
                    console.log ('The websocket is closed!');
                } ,
                error: function (ws, error) {
                    Ext.Error.raise (error);
                } ,
                message: function (ws, message) {
                    me.getPrimarychart().getStore().setData(message);
                }
            }
        });
    }

});